import org.apache.nifi.processor.io.StreamCallback
import groovy.json.*
import java.util.concurrent.*
import java.net.URI
import org.java_websocket.client.WebSocketClient
import org.java_websocket.handshake.ServerHandshake
import groovy.transform.Field


// ---------------------------------------------------------
// SHARED STATE (persists across processor invocations)
// ---------------------------------------------------------
@Field static WebSocketClient wsClient = null
@Field static boolean wsConnected = false
@Field static boolean processorActive = true
@Field static def messageQueue = new LinkedBlockingQueue<String>(10000)
@Field static long connectionStartTime = 0
@Field static int reconnectAttempts = 0
@Field static final int MAX_RECONNECT_ATTEMPTS = 5
@Field static final long RECONNECT_DELAY_MS = 5000


// ---------------------------------------------------------
// CREATE FLOWFILE
// ---------------------------------------------------------
def createFlowFile(session, log, trade, relSuccess) {
    try {
        def jsonStr = JsonOutput.toJson(trade)
        def flowFile = session.create()

        flowFile = session.write(flowFile, { _, out ->
            out.write(jsonStr.getBytes("UTF-8"))
        } as StreamCallback)

        // Key attributes for downstream routing/partitioning
        flowFile = session.putAttribute(flowFile, "symbol", String.valueOf(trade.s))
        flowFile = session.putAttribute(flowFile, "price", String.valueOf(trade.p))
        flowFile = session.putAttribute(flowFile, "quantity", String.valueOf(trade.q))
        flowFile = session.putAttribute(flowFile, "event_time", String.valueOf(trade.T))
        flowFile = session.putAttribute(flowFile, "timestamp", String.valueOf(trade.T))

        session.transfer(flowFile, relSuccess)
        log.info("Sent FlowFile (aggTrade): ${trade.s} price=${trade.p} qty=${trade.q}")

    } catch (Exception e) {
        log.error("Failed to create FlowFile: ${e.message}", e)
    }
}


// ---------------------------------------------------------
// START WEBSOCKET (only if not already connected)
// ---------------------------------------------------------
def startWebSocket(log) {
    // If already connected, don't reconnect
    if (wsClient != null && wsConnected) {
        return true
    }

    // If connection exists but not connected, clean it up
    if (wsClient != null && !wsConnected) {
        try {
            wsClient.close()
        } catch (Exception e) {
            // Ignore cleanup errors
        }
        wsClient = null
    }

    // Check reconnect attempts
    if (reconnectAttempts >= MAX_RECONNECT_ATTEMPTS) {
        log.error("Max reconnect attempts (${MAX_RECONNECT_ATTEMPTS}) reached. Waiting before retry...")
        reconnectAttempts = 0
        return false
    }

    final def nifiLog = log

    try {
        wsClient = new WebSocketClient(new URI("wss://stream.binance.com:9443/stream")) {

            @Override
            void onOpen(ServerHandshake hs) {
                wsConnected = true
                connectionStartTime = System.currentTimeMillis()
                reconnectAttempts = 0
                nifiLog.info("WebSocket connected successfully")

                // Subscribe to aggTrade stream (BTCUSDT only)
                send(JsonOutput.toJson([
                    method: "SUBSCRIBE",
                    params: [
                        "btcusdt@aggTrade"
                    ],
                    id: 1
                ]))
                nifiLog.info("Subscribed to aggTrade stream: btcusdt@aggTrade")
            }

            @Override
            void onMessage(String msg) {
                if (!processorActive) return
                // Add message to queue (non-blocking)
                if (!messageQueue.offer(msg)) {
                    nifiLog.warn("Message queue full, dropping message")
                }
            }

            @Override
            void onClose(int code, String reason, boolean remote) {
                wsConnected = false
                nifiLog.warn("WebSocket closed: code=${code}, reason=${reason}, remote=${remote}")

                // Auto-reconnect if processor is still active
                if (processorActive && reconnectAttempts < MAX_RECONNECT_ATTEMPTS) {
                    reconnectAttempts++
                    nifiLog.info("Attempting reconnect ${reconnectAttempts}/${MAX_RECONNECT_ATTEMPTS} in ${RECONNECT_DELAY_MS}ms...")
                    wsClient = null

                    // Schedule reconnect (will happen on next processor execution)
                    Thread.start {
                        Thread.sleep(RECONNECT_DELAY_MS)
                    }
                } else {
                    wsClient = null
                }
            }

            @Override
            void onError(Exception ex) {
                wsConnected = false
                nifiLog.error("WebSocket error: ${ex.message}")
                // Don't set wsClient to null here, let onClose handle cleanup
            }
        }

        wsClient.connect()
        nifiLog.info("Initiating WebSocket connection...")

        // Wait a bit for connection to establish
        Thread.sleep(2000)

        return wsConnected

    } catch (Exception e) {
        log.error("WebSocket connect failed: ${e.message}", e)
        wsClient = null
        reconnectAttempts++
        return false
    }
}


// ---------------------------------------------------------
// MAIN EXECUTION
// ---------------------------------------------------------
try {
    processorActive = true

    // Ensure WebSocket is connected (only connects if not already connected)
    if (!startWebSocket(log)) {
        // Connection failed, yield and try again next time
        return
    }

    // Process messages from queue (non-blocking)
    int processed = 0
    int maxProcess = 500  // Process up to 500 messages per execution (trades can be high-volume)

    while (processed < maxProcess) {
        def msg = messageQueue.poll()  // Non-blocking poll
        if (!msg) break

        def json
        try {
            json = new JsonSlurper().parseText(msg)
        } catch (Exception e) {
            log.warn("JSON parse error: ${e.message}")
            continue
        }

        // Process aggTrade events
        if (json?.data?.e == "aggTrade") {
            createFlowFile(session, log, json.data, REL_SUCCESS)
            processed++
        }
    }

    if (processed > 0) {
        log.info("Processed ${processed} aggTrade messages")
    }

} catch (Exception e) {
    log.error("Fatal processor error: ${e.message}", e)
}
